def longest_dup_substring(s: str) -> str:
    n = len(s)
    # Create and initialize the DP table
    dp = [[0] * (n + 1) for _ in range(n + 1)]

    max_len = 0
    end_index = 0

    # Build the DP table
    for i in range(1, n + 1):
        for j in range(1, n + 1):
            # If characters match and they are not the same index in original string
            if s[i - 1] == s[j - 1] and i != j:
                dp[i][j] = dp[i - 1][j - 1] + 1
                # Update the maximum length and ending index of longest repeated substring
                if dp[i][j] > max_len:
                    max_len = dp[i][j]
                    end_index = i - 1
            else:
                dp[i][j] = 0

    # Extract the longest repeated substring
    return s[end_index - max_len + 1:end_index + 1] if max_len > 0 else ""

